mapper.select:
select g1.title, g1.price, g1.board_idx, g1.category3_idx, g1.category2_idx, g1.state, g1.status, b.like_count, b.email, (select full_name from file_list where board_idx = b.idx limit 1) as full_name
from board b, game g1 where b.idx = g1.board_idx
from game g1, board b, category2 c2 where b.idx = g1.board_idx and g1.category2_idx = c2.idx and c2.category_idx = #{categoryIdx}
from game g1, board b, category2 c2 where b.idx = g1.board_idx and g1.category2_idx = c2.idx and c2.category_idx = #{categoryIdx} and c2.idx = #{category2Idx} and g1.category3_idx = #{category3Idx}
order by g1.idx desc
